package org.esciencecloud.facade.escienceclouddb;import org.esciencecloud.jpa.escienceclouddb.Person;import javax.persistence.EntityManager;import javax.persistence.EntityManagerFactory;import javax.persistence.Persistence;import javax.persistence.Query;import java.util.ArrayList;import java.util.List;import com.fasterxml.jackson.databind.ObjectMapper;public class PersonFacade{private final org.slf4j.Logger logger = org.slf4j.LoggerFactory.getLogger(PersonFacade.class);public boolean createPerson(Person entity){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.persist(entity);em.getTransaction().commit();em.close();emf.close();return true;}public boolean updatePerson(Person entity){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.merge(entity);em.getTransaction().commit();em.close();emf.close();return true;}public boolean deletePersonById(int id){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.remove(em.find(Person.class, id));em.getTransaction().commit();em.close();emf.close();return true;}public String listAllActivePerson (){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();List<Person> entityList = new ArrayList<>();Query nq1 = em.createNamedQuery("Person.findByActive");nq1.setParameter("active",1);entityList.addAll(nq1.getResultList());em.close();emf.close();return entityListToJson(entityList);}public String listAllPerson (){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();List<Person> entityList = new ArrayList<>();Query nq1 = em.createNamedQuery("Person.findAll");entityList.addAll(nq1.getResultList());em.close();emf.close();return entityListToJson(entityList);}public String entityToJson(Person entity){String json = null;ObjectMapper mapper = new ObjectMapper();try {json = mapper.writeValueAsString(entity);} catch (java.io.IOException e) {e.printStackTrace();}return json;}public String entityListToJson(List<Person> entityList){String json = null;ObjectMapper mapper = new ObjectMapper();try {json = mapper.writeValueAsString(entityList);} catch (java.io.IOException e) {e.printStackTrace();}return json;}}