package org.esciencecloud.facade.escienceclouddb;import org.esciencecloud.jpa.escienceclouddb.Project;import javax.persistence.EntityManager;import javax.persistence.EntityManagerFactory;import javax.persistence.Persistence;import javax.persistence.Query;import java.util.ArrayList;import java.util.List;import com.fasterxml.jackson.databind.ObjectMapper;public class ProjectFacade{private final org.slf4j.Logger logger = org.slf4j.LoggerFactory.getLogger(ProjectFacade.class);public boolean createProject(Project entity){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.persist(entity);em.getTransaction().commit();em.close();emf.close();return true;}public boolean updateProject(Project entity){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.merge(entity);em.getTransaction().commit();em.close();emf.close();return true;}public boolean deleteProjectById(int id){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();em.getTransaction().begin();em.remove(em.find(Project.class, id));em.getTransaction().commit();em.close();emf.close();return true;}public String listAllActiveProject (){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();List<Project> entityList = new ArrayList<>();Query nq1 = em.createNamedQuery("Project.findByActive");nq1.setParameter("active",1);entityList.addAll(nq1.getResultList());em.close();emf.close();return entityListToJson(entityList);}public String listAllProject (){EntityManagerFactory emf = Persistence.createEntityManagerFactory("eScienceCloudJPAPU");EntityManager em = emf.createEntityManager();List<Project> entityList = new ArrayList<>();Query nq1 = em.createNamedQuery("Project.findAll");entityList.addAll(nq1.getResultList());em.close();emf.close();return entityListToJson(entityList);}public String entityToJson(Project entity){String json = null;ObjectMapper mapper = new ObjectMapper();try {json = mapper.writeValueAsString(entity);} catch (java.io.IOException e) {e.printStackTrace();}return json;}public String entityListToJson(List<Project> entityList){String json = null;ObjectMapper mapper = new ObjectMapper();try {json = mapper.writeValueAsString(entityList);} catch (java.io.IOException e) {e.printStackTrace();}return json;}}